import{_ as e,o as a,c as r,ag as n}from"./chunks/framework.DyNZgC5I.js";const u=JSON.parse('{"title":"测点类型","description":"","frontmatter":{},"headers":[],"relativePath":"guide/point-types.md","filePath":"guide/point-types.md","lastUpdated":1769824112000}'),d={name:"guide/point-types.md"};function i(o,t,s,l,p,h){return a(),r("div",null,[...t[0]||(t[0]=[n(`<h1 id="测点类型" tabindex="-1">测点类型 <a class="header-anchor" href="#测点类型" aria-label="Permalink to &quot;测点类型&quot;">​</a></h1><p>EMS Simulate 支持四种测点类型，覆盖工业自动化中常见的数据采集和控制需求。</p><h2 id="类型概览" tabindex="-1">类型概览 <a class="header-anchor" href="#类型概览" aria-label="Permalink to &quot;类型概览&quot;">​</a></h2><table tabindex="0"><thead><tr><th>类型</th><th>代码</th><th>说明</th><th>典型用途</th></tr></thead><tbody><tr><td><strong>遥测 (YC)</strong></td><td>frame_type=0</td><td>模拟量测量</td><td>电压、电流、功率、温度</td></tr><tr><td><strong>遥信 (YX)</strong></td><td>frame_type=1</td><td>开关量状态</td><td>运行状态、故障标志</td></tr><tr><td><strong>遥控 (YK)</strong></td><td>frame_type=2</td><td>开关量命令</td><td>启停命令、合分闸</td></tr><tr><td><strong>遥调 (YT)</strong></td><td>frame_type=3</td><td>模拟量命令</td><td>功率设定、温度设定</td></tr></tbody></table><h2 id="遥测-yc-telemetering" tabindex="-1">遥测 (YC - Telemetering) <a class="header-anchor" href="#遥测-yc-telemetering" aria-label="Permalink to &quot;遥测 (YC - Telemetering)&quot;">​</a></h2><p>用于采集模拟量测量值，如电压、电流、功率、温度等连续变化的物理量。</p><p><strong>特点：</strong></p><ul><li>支持浮点数和整数类型</li><li>可配置乘法系数和加法系数</li><li>支持多种数据解析格式</li></ul><p><strong>系数转换公式：</strong></p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>真实值 = 寄存器值 × 乘法系数 + 加法系数</span></span>
<span class="line"><span>寄存器值 = (真实值 - 加法系数) ÷ 乘法系数</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="遥信-yx-status-indication" tabindex="-1">遥信 (YX - Status Indication) <a class="header-anchor" href="#遥信-yx-status-indication" aria-label="Permalink to &quot;遥信 (YX - Status Indication)&quot;">​</a></h2><p>用于采集开关量状态，如设备运行/停止、开关分/合、故障/正常等二值状态。</p><p><strong>特点：</strong></p><ul><li>值通常为 0 或 1</li><li>可配置反转逻辑</li><li>支持状态变化事件</li></ul><h2 id="遥控-yk-remote-control" tabindex="-1">遥控 (YK - Remote Control) <a class="header-anchor" href="#遥控-yk-remote-control" aria-label="Permalink to &quot;遥控 (YK - Remote Control)&quot;">​</a></h2><p>用于发送开关量控制命令，如启动/停止设备、合闸/分闸等。</p><p><strong>特点：</strong></p><ul><li>支持&quot;选择-执行&quot;两步操作</li><li>可配置命令超时</li><li>支持返校验证</li></ul><h2 id="遥调-yt-setpoint" tabindex="-1">遥调 (YT - Setpoint) <a class="header-anchor" href="#遥调-yt-setpoint" aria-label="Permalink to &quot;遥调 (YT - Setpoint)&quot;">​</a></h2><p>用于发送模拟量设定值，如功率设定、温度设定等。</p><p><strong>特点：</strong></p><ul><li>支持范围校验</li><li>支持步进值限制</li><li>可配置系数转换</li></ul><h2 id="数据模拟方式" tabindex="-1">数据模拟方式 <a class="header-anchor" href="#数据模拟方式" aria-label="Permalink to &quot;数据模拟方式&quot;">​</a></h2><p>每种测点都支持多种数据模拟方式：</p><table tabindex="0"><thead><tr><th>模拟方式</th><th>说明</th></tr></thead><tbody><tr><td><strong>固定值</strong></td><td>保持固定数值不变</td></tr><tr><td><strong>随机模拟</strong></td><td>在指定范围内随机变化</td></tr><tr><td><strong>步进模拟</strong></td><td>按指定步长递增/递减</td></tr><tr><td><strong>正弦模拟</strong></td><td>按正弦曲线变化</td></tr></tbody></table>`,25)])])}const g=e(d,[["render",i]]);export{u as __pageData,g as default};
