import{_ as a,o as d,c as e,ag as r}from"./chunks/framework.DyNZgC5I.js";const b=JSON.parse('{"title":"协议支持","description":"","frontmatter":{},"headers":[],"relativePath":"guide/protocols.md","filePath":"guide/protocols.md","lastUpdated":1769824112000}'),o={name:"guide/protocols.md"};function l(i,t,h,s,n,c){return d(),e("div",null,[...t[0]||(t[0]=[r('<h1 id="协议支持" tabindex="-1">协议支持 <a class="header-anchor" href="#协议支持" aria-label="Permalink to &quot;协议支持&quot;">​</a></h1><p>EMS Simulate 支持多种工业通信协议，可模拟服务端和客户端两种角色。</p><h2 id="协议概览" tabindex="-1">协议概览 <a class="header-anchor" href="#协议概览" aria-label="Permalink to &quot;协议概览&quot;">​</a></h2><table tabindex="0"><thead><tr><th>协议</th><th>服务端</th><th>客户端</th><th>默认端口</th></tr></thead><tbody><tr><td>Modbus TCP</td><td>✅</td><td>✅</td><td>502</td></tr><tr><td>Modbus RTU</td><td>✅</td><td>✅</td><td>串口</td></tr><tr><td>IEC 60870-5-104</td><td>✅</td><td>✅</td><td>2404</td></tr><tr><td>DLT/T 645-2007</td><td>✅</td><td>✅</td><td>8899</td></tr></tbody></table><h2 id="modbus-tcp-rtu" tabindex="-1">Modbus TCP/RTU <a class="header-anchor" href="#modbus-tcp-rtu" aria-label="Permalink to &quot;Modbus TCP/RTU&quot;">​</a></h2><p>基于 <code>pymodbus</code> 库实现，支持：</p><ul><li><strong>功能码 03</strong>: 读保持寄存器</li><li><strong>功能码 04</strong>: 读输入寄存器</li><li><strong>功能码 06</strong>: 写单个寄存器</li><li><strong>功能码 16</strong>: 写多个寄存器</li><li><strong>功能码 01/02</strong>: 读线圈/离散输入</li><li><strong>功能码 05/15</strong>: 写单个/多个线圈</li></ul><h3 id="服务端模式" tabindex="-1">服务端模式 <a class="header-anchor" href="#服务端模式" aria-label="Permalink to &quot;服务端模式&quot;">​</a></h3><p>模拟从设备（Slave），响应主站轮询请求。</p><h3 id="客户端模式" tabindex="-1">客户端模式 <a class="header-anchor" href="#客户端模式" aria-label="Permalink to &quot;客户端模式&quot;">​</a></h3><p>模拟主站（Master），主动轮询从设备。</p><h2 id="iec-60870-5-104" tabindex="-1">IEC 60870-5-104 <a class="header-anchor" href="#iec-60870-5-104" aria-label="Permalink to &quot;IEC 60870-5-104&quot;">​</a></h2><p>电力系统标准通信协议，基于 <code>c104</code> 库实现。</p><h3 id="支持的-asdu-类型" tabindex="-1">支持的 ASDU 类型 <a class="header-anchor" href="#支持的-asdu-类型" aria-label="Permalink to &quot;支持的 ASDU 类型&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>M_ME_NC_1 (13)</td><td>测量值短浮点数</td></tr><tr><td>M_SP_NA_1 (1)</td><td>单点信息</td></tr><tr><td>C_SC_NA_1 (45)</td><td>单点命令</td></tr><tr><td>C_SE_NC_1 (50)</td><td>设定点命令短浮点数</td></tr></tbody></table><h2 id="dlt-t-645-2007" tabindex="-1">DLT/T 645-2007 <a class="header-anchor" href="#dlt-t-645-2007" aria-label="Permalink to &quot;DLT/T 645-2007&quot;">​</a></h2><p>国家电网智能电能表通信协议，支持：</p><ul><li>电能数据读取</li><li>需量数据读取</li><li>事件记录读取</li><li>参数设置</li></ul><h3 id="数据标识" tabindex="-1">数据标识 <a class="header-anchor" href="#数据标识" aria-label="Permalink to &quot;数据标识&quot;">​</a></h3><p>采用 4 字节 BCD 编码的数据标识符，如：</p><ul><li><code>00010000</code> - 正向有功总电能</li><li><code>02010100</code> - A相电压</li></ul>',21)])])}const p=a(o,[["render",l]]);export{b as __pageData,p as default};
